Function Dec64 { Param($a) $b = [System.Text.Encoding]::ASCII.GetString([System.Convert]::FromBase64String($a));Return $b };
$appData = (Dec64 '') | ConvertFrom-Csv -Delimiter ',' -Header App,RemoveCMD,DeProv,Yellow

Export-StartLayout -Path "C:\Temp\LayoutModification.json"
GC C:\temp\LayoutModification.json


