#region - Get-ZipCompressionRatio
    $file = "C:\Users\adminCM\Desktop\PAW DLs\npp.8.6.7.portable.x64.zip"
    Add-Type -AssemblyName System.IO.Compression.FileSystem
    [System.IO.Compression.ZipFile]::Open("$file",'Read') | ForEach-Object {
        $_.Entries | ForEach-Object `
                        -Begin {
                            [long]$TotalCompressed = $null
                            [long]$TotalSize = $null
                            } `
                        -Process {
                            $TotalCompressed += $_.CompressedLength
                            $TotalSize += $_.Length
                            } `
                        -End {
                            [pscustomobject]@{
                                FileSize = "{0:N2} MB" -f ($TotalSize/1048576)
                                CompressedSize = "{0:N2} MB" -f ($TotalCompressed/1048576)
                                Ratio = "{0:P2}" -f ($TotalCompressed / $TotalSize)
                                Reduction = "{0:P2}" -f (1-($TotalCompressed / $TotalSize))
                                }
                            }
        } 

#endregion
